import React, {Fragment, useEffect, useState} from 'react';
import Alert from "./ui-components/Alert";
import Select from "./form-components/Select";
import Input from "./form-components/Input";
import {Link} from "react-router-dom";

function InputProduction(props) {
    const [input, setInput] = useState({
        chick: "",
        id: 0,
        title: "",
        eggs: "",
        dirty: "",
        wrong_shape: "",
        weak_shell: "",
        damaged: "",
        hatching_eggs: "",
        Premises: {},
    });
    const [error] = useState(null);
    const [errors, setErrors] = useState(null);
    const [alert, setAlert] = useState({type: "d-none", message: ""})
    const chickOptions = [
        { id: "Kuroiler", value: "Kuroiler" },
        { id: "RainbowRooster", value: "RainbowRooster" },
        { id: "Broiler", value: "Broiler" },
        { id: "Layer", value: "Layer" },
    ]
    const farmOptions = [
        {id: "Kaptagat", value: "Kaptagat"},
        {id: "Nyaru", value: "Nyaru"},
        {id: "Nandi", value: "Nandi"},
    ];

    useEffect(() =>{

    }, [])

    const handleChange = (e) => {
        let value = e.target.value;
        let name = e.target.name;
        setInput({
            ...input,
            [name]: value,
        });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData(e.currentTarget);
        console.log("title", formData.get("title"));
        console.log("eggs", formData.get("eggs"));
        console.log("dirty", formData.get("dirty"));
        const data_ = Object.fromEntries(formData.entries());
        console.log(data_);
        const dirty = parseInt(formData.get("dirty")) // string converted to int

        return ;
        let errors = [];
        if (input.title === "") {
            errors.push("title") //validating title only
        }
        setErrors(errors)
        if (errors.length > 0) {
            return false
        }

        const data = new FormData(e.target)
        const payload = Object.fromEntries(data.entries());
        const myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        //jwt here

        const requestOptions = {
            method: "POST",
            body: JSON.stringify(payload),
            headers: myHeaders,
        }
        // axios ?
        // token = ''
        // config = {
        //     'Content-Type': 'application/json',
        //     'Headers': {
        //         'Authorization': `Token ${token}`
        //     }
        // }
        // axios.post('url-here', body, config)
        fetch(`https://flockmanager.herokuapp.com/v1/kukuchic/${chickOptions}`, requestOptions)
            .then((response) => response.json())
            .then((data) => {
                if (data.error) {
                    setAlert({
                        alert: {type: "alert-danger", message: data.error.message},
                    })
                } else {
                    props.history.push({
                        pathname: "/all_chicks"
                    });
                }
            });


    };
    function hasError() {
        return errors;
    }

    if (error !== null) {
        return <div>Error: {error.message}</div>
    } else { // not necessary
        return (
            <Fragment>
                <h2>Add/Edit Production</h2>
                <Alert
                    alertType={alert.type}
                    alertMessage={alert.message}
                />
                <hr/>
                <form onSubmit={handleSubmit}>
                    <input
                        type="hidden"
                        name="id"
                        id="id"
                        defaultValue={input.id}
                        readOnly
                    />

                    <Select
                        title={"Flock"}
                        name="chick"
                        options={chickOptions}
                        value={input.chick}
                        onChange={handleChange}
                        placeholder="Choose..."
                    />

                    <Input
                        title={"Title"}
                        className={hasError("title") ? "is-invalid" : ""}
                        type="text"
                        name="title"
                        value={input.title}
                        onChange={handleChange}
                        errorDiv={hasError("title") ? "text-danger" : "d-none"}
                        errorMsg={"Please enter a title"}
                    />

                    <Input
                        title="Number of Eggs"
                        type="number"
                        name="eggs"
                        value={input.eggs}
                        onChange={handleChange}
                    />

                    <Input
                        title="Dirty Eggs"
                        type="number"
                        name="dirty"
                        value={input.dirty}
                        onChange={handleChange}
                    />

                    <Input
                        title="Wrong Shape"
                        type="number" // the rest you can change, lemme demonstrate
                        name="wrong_shape"
                        value={input.wrong_shape}
                        onChange={handleChange}
                    />
                    <Input
                        title={"Weak Shell"}
                        type="number"
                        name={"weak_shell"}
                        value={input.weak_shell}
                        onChange={handleChange}
                    />

                    <Input
                        title={"Damaged Eggs"}
                        type="number"
                        name={"damaged"}
                        value={input.damaged}
                        onChange={handleChange}
                    />

                    <Input
                        title={"Total Hatching Eggs"}
                        type="number"
                        name={"hatching_eggs"}
                        value={input.hatching_eggs}
                        onChange={handleChange}
                    />

                    <Select
                        title={"Farm"}
                        name={"Premises"}
                        options={farmOptions}
                        value={input.Premises.farm}
                        onChange={handleChange}
                        placeholder="Select farm..."
                    />
                    <Input
                        title={"House Number"}
                        type={"text"}
                        name={"house"}
                        value={input.Premises.house}
                        onChange={handleChange}
                    />
                    <button className="btn btn-primary">Save</button>
                    <Link to="/all_chicks" className="btn btn-warning ms-1">
                        Cancel
                    </Link>
                </form>
            </Fragment>
        );
    }
}

export default InputProduction
