import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";


export default class OneGenre extends Component {
    state = {
        isLoaded: false,
        error: null,
        chickName: "",
    };

    componentDidMount() {
        // consider using axios->3rd party package, way easier than fetch to handle errors
        // "http://localhost:9000/v1/kukuchic/flock/"+ this.props.match.params.id
        const chick = this.props.location;
        fetch(`http://localhost:9000/v1/kukuchic/${chick}`)
            .then((response) => {
                if (response.status !== "200") {
                    let err = Error;
                    err.message = "Invalid response code: " + response.status;
                    this.setState({ error: err });
                }
                return response.json();
            })
            .then((json) => {
                this.setState(
                    {
                        flock: json.flock,
                        isLoaded: true,
                        chickName: this.props.location.chickName,
                    },
                    (error) => {
                        this.setState({
                            isLoaded: true,
                            error,
                        });
                    }
                );
            });
    }

    render() {
        let { flock, isLoaded, error, chickName } = this.state;

        // if (!flock) {
        //     flock = [];
        // }

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <p>Loading...</p>;
        } else {
            console.log(this.state.flock);
            return (
                <Fragment>
                    <h2>Chick:{chickName}</h2>

                    <div className="list-group">
                        <Link
                            key={flock.operations.op_pro}
                            to={{
                                pathname: this.props.location.pathname + "/production",
                                state: {
                                    // the backend result does not have Production property
                                    production: this.state.flock.Production, 
                                },

                            }}
                            className="list-group-item list-group-item-action"
                        >
                            Production

                        </Link>
                        <Link
                            key={flock.operations.op_hat}
                            to={{
                                pathname: this.props.location.pathname + "/hatchery",
                                state: {
                                    production: this.state.flock.Hatchery,
                                },

                            }}
                            className="list-group-item list-group-item-action"
                        >
                            Hatchery
                        </Link>
                    </div>
                </Fragment>
            );

        }
    }
}
