import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";

export default class Chicks extends Component {
    state = {
        genres: [],
        isLoaded: false,
        error: null,
    };

    componentDidMount() {
        fetch("http://localhost:8000/v1/genres")
            .then((response) => {
                if (response.status !== "200") {
                    let err = Error;
                    err.message = "Invalid response code: " + response.status;
                    this.setState({ error: err });
                }
                return response.json();
            })
            .then((json) => {
                this.setState(
                    {
                        genres: json.genres,
                        isLoaded: true,
                    },
                    (error) => {
                        this.setState({
                            isLoaded: true,
                            error,
                        });
                    }
                );
            });
    }

    render() {
            return (
                <Fragment>
                    <h2>Chicks</h2>

                    <div className="list-group">
                        
                    </div>
                </Fragment>
            );
        }
}
