import React, {Fragment, useEffect, useState} from 'react';
import Alert from "./ui-components/Alert";
import Select from "./form-components/Select";
import Input from "./form-components/Input";

function InputProduction(props) {
    const [input, setInput] = useState({});
    const [error, setError] = useState(null);
    const [errors, setErrors] = useState(null);
    const [alert, setAlert] = useState({type: "d-none", message: ""})
    const chickOptions = [
        { id: "Kuroiler", value: "Kuroiler" },
        { id: "RainbowRooster", value: "RainbowRooster" },
        { id: "Broiler", value: "Broiler" },
        { id: "Layer", value: "Layer" },
    ]
    const farmOptions = [
        {id: "Kaptagat", value: "Kaptagat"},
        {id: "Nyaru", value: "Nyaru"},
        {id: "Nandi", value: "Nandi"},
    ];

    useEffect(() =>{

    })

    const handleChange = () => (e) => {
        let value = e.target.value;
        let name = e.target.name;
        setInput({
            ...input,
            [name]: value,
        });
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        let errors = [];
        if (input.title === "") {
            errors.push("title") //validating title only
        }
        setErrors(errors)
        if (errors.length > 0) {
            return false
        }

        const data = new FormData(e.target)
        const payload = Object.fromEntries(data.entries());
        const myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        //jwt here

        const requestOptions = {
            method: "POST",
            body: JSON.stringify(payload),
            headers: myHeaders,
        }
        fetch(`https://flockmanager.herokuapp.com/v1/kukuchic/${chickOptions}`, requestOptions)
            .then((response) => response.json())
            .then((data) => {
                if (data.error) {
                    setAlert({
                        alert: {type: "alert-danger", message: data.error.message},
                    })
                } else {
                    props.history.push({
                        pathname: "/all_chicks"
                    });
                }
            });


    };
    function hasError() {
        return errors;
    }

    if (error !== null) {

        return <div>Error: {error.message}</div>

    } else {
        return (
            <Fragment>
                <h2>Add/Edit Hatchery</h2>
                <Alert
                    alertType={alert.type}
                    alertMessage={alert.message}
                />
                <hr/>
                <form onSubmit={handleSubmit}>
                    <input
                        type="hidden"
                        name="id"
                        id="id"
                        value={input.id}
                        onChange={input.id}
                    />

                    <Select
                        title={"Flock"}
                        name={"chickName"}
                        options={chickOptions}
                        value={input.chickName}
                        handleChange={handleChange()}
                        placeholder="Choose..."

                    />

                    <Input
                        title={"Title"}
                        className={hasError("title") ? "is-invalid" : ""}
                        type={"text"}
                        name={"title"}
                        value={input.title}
                        handleChange={handleChange("title")}
                        errorDiv={hasError("title") ? "text-danger" : "d-none"}
                        errorMsg={"Please enter a title"}
                    />

                    <Input
                        title={"Number of Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.eggs}
                        handleChange={handleChange("eggs")}
                    />

                    <Input
                        title={"Dirty Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.dirty}
                        handleChange={handleChange("dirty")}
                    />

                    <Input
                        title={"Wrong Shape"}
                        type={"text"}
                        name={"rating"}
                        value={input.wrong_shape}
                        handleChange={handleChange("wrong_shape")}
                    />
                    <Input
                        title={"Weak Shell"}
                        type={"text"}
                        name={"runtime"}
                        value={input.weak_shell}
                        handleChange={handleChange("weak_shell")}
                    />

                    <Input
                        title={"Damaged Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.damaged}
                        handleChange={handleChange("damaged")}
                    />

                    <Input
                        title={"Total Hatching Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.hatching_eggs}
                        handleChange={handleChange("hatching_eggs")}
                    />

                    <Select
                        title={"Farm"}
                        name={"Premises"}
                        options={farmOptions}
                        value={input.Premises}
                        handleChange={handleChange("Premises")}
                        placeholder="Select Farm..."
                    />
                    <Select
                        title={"House"}
                        name={"Premises"}
                        options={farmOptions}
                        value={input.Premises}
                        handleChange={handleChange("Premises")}
                        placeholder="Select Farm..."
                    />
                </form>
            </Fragment>
        );
    }
}

export default InputProduction
