import React, {Fragment, useState} from 'react';
import Alert from "./ui-components/Alert";
import Select from "./form-components/Select";
import Input from "./form-components/Input";
import {Link} from "react-router-dom";

export default function InputProduction() {
    const [data, setData] = useState({
        Production: {
            chickName: "",
            id: 0,
            title: "",
            eggs: "",
            dirty: "",
            wrong_shape: "",
            weak_shell: "",
            damaged: "",
            hatching_eggs: "",
            Premises: "",
        },
        chickOptions: [
            { id: "Kuroiler", value: "Kuroiler" },
            { id: "RainbowRooster", value: "RainbowRooster" },
            { id: "Broiler", value: "Broiler" },
            { id: "Layer", value: "Layer" },
        ],
        farmOptions:[
            { id: "Kaptagat", value: "Kaptagat" },
            { id: "Nyaru", value: "Nyaru" },
            { id: "Nandi", value: "Nandi" },
        ],
    });
    let { Chicks, isLoaded, error } = this.state;

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <p>Loading...</p>;
    } else {
        return (
            <Fragment>
                <h2>Add/Edit Production</h2>
                <Alert
                    alertType={this.state.alert.type}
                    alertMessage={this.state.alert.message}
                />
                <hr />
                <form onSubmit={this.handleSubmit}>
                    <input
                        type="hidden"
                        name="id"
                        id="id"
                        value={Chicks.id}
                        onChange={this.handleChange}
                    />

                    <Select
                        title={"Flock"}
                        name={"chickName"}
                        options={this.state.chickOptions}
                        value={Chicks.chickName}
                        handleChange={this.handleChange}
                        placeholder="Choose..."

                    />

                    <Input
                        title={"Title"}
                        className={this.hasError("title") ? "is-invalid" : ""}
                        type={"text"}
                        name={"title"}
                        value={Chicks.title}
                        handleChange={this.handleChange}
                    />

                    <Input
                        title={"Number of Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={Chicks.eggs}
                        handleChange={this.handleChange}
                    />

                    <Input
                        title={"Dirty Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={Chicks.dirty}
                        handleChange={this.handleChange}
                    />

                    <Input
                        title={"Wrong Shape"}
                        type={"text"}
                        name={"rating"}
                        value={Chicks.wrong_shape}
                        handleChange={this.handleChange}
                    />
                    <Input
                        title={"Weak Shell"}
                        type={"text"}
                        name={"runtime"}
                        value={Chicks.weak_shell}
                        handleChange={this.handleChange}
                    />

                    <Input
                        title={"Damaged Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={Chicks.damaged}
                        handleChange={this.handleChange}
                    />

                    <Input
                        title={"Total Hatching Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={Chicks.hatching_eggs}
                        handleChange={this.handleChange}
                    />

                    <Select
                        title={"Farm"}
                        options={this.state.farmOptions}
                        value={Chicks.Premises}
                        handleChange={this.handleChange}
                        placeholder="Select Farm..."
                    />

                    <hr />

                    <button className="btn btn-primary">Save</button>
                    <Link to="/all_chicks" className="btn btn-warning ms-1">
                        Cancel
                    </Link>
                </form>
            </Fragment>
        );
    }
}
