import React, {Fragment, useState} from 'react';
import Alert from "./ui-components/Alert";
import Select from "./form-components/Select";
import Input from "./form-components/Input";
import {Link} from "react-router-dom";

export default function InputProduction() {
    const [input, setInput] = useState({
        Production: {
            chickName: "",
            id: 0,
            title: "",
            eggs: "",
            dirty: "",
            wrong_shape: "",
            weak_shell: "",
            damaged: "",
            hatching_eggs: "",
            Premises: "",
        },
        chickOptions: [
            { id: "Kuroiler", value: "Kuroiler" },
            { id: "RainbowRooster", value: "RainbowRooster" },
            { id: "Broiler", value: "Broiler" },
            { id: "Layer", value: "Layer" },
        ],
        farmOptions:[
            { id: "Kaptagat", value: "Kaptagat" },
            { id: "Nyaru", value: "Nyaru" },
            { id: "Nandi", value: "Nandi" },
        ],
    });
    function handleSubmit() {

    }


    function handleChange(e) {
        const newInput={...input}
        newInput[e.target.id] = e.target.value;
        setInput(newInput)

        console.log(newInput)
    }


    // let { Chicks, isLoaded, error } = this.state;
    //
    // if (error) {
    //     return <div>Error: {error.message}</div>;
    // } else if (!isLoaded) {
    //     return <p>Loading...</p>;
    // } else {
        return (
            <Fragment>
                <h2>Add/Edit Production</h2>
                <Alert
                    alertType={alert.type}
                    alertMessage={alert.message}
                />
                <hr />
                <form onSubmit={handleSubmit}>
                    <input
                        type="hidden"
                        name="id"
                        id="id"
                        value={input.id}
                        onChange={handleChange}
                    />

                    <Select
                        title={"Flock"}
                        name={"chickName"}
                        options={this.state.chickOptions}
                        value={input.chickName}
                        handleChange={handleChange}
                        placeholder="Choose..."

                    />

                    <Input
                        title={"Title"}
                        className={this.hasError("title") ? "is-invalid" : ""}
                        type={"text"}
                        name={"title"}
                        value={input.title}
                        handleChange={handleChange}
                    />

                    <Input
                        title={"Number of Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.eggs}
                        handleChange={handleChange}
                    />

                    <Input
                        title={"Dirty Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.dirty}
                        handleChange={handleChange}
                    />

                    <Input
                        title={"Wrong Shape"}
                        type={"text"}
                        name={"rating"}
                        value={input.wrong_shape}
                        handleChange={handleChange}
                    />
                    <Input
                        title={"Weak Shell"}
                        type={"text"}
                        name={"runtime"}
                        value={input.weak_shell}
                        handleChange={handleChange}
                    />

                    <Input
                        title={"Damaged Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.damaged}
                        handleChange={handleChange}
                    />

                    <Input
                        title={"Total Hatching Eggs"}
                        type={"text"}
                        name={"runtime"}
                        value={input.hatching_eggs}
                        handleChange={handleChange}
                    />

                    <Select
                        title={"Farm"}
                        options={this.state.farmOptions}
                        value={input.Premises}
                        handleChange={handleChange}
                        placeholder="Select Farm..."
                    />

                    <hr />

                    <button className="btn btn-primary">Save</button>
                    <Link to="/all_chicks" className="btn btn-warning ms-1">
                        Cancel
                    </Link>
                </form>
            </Fragment>
        );
}
