import React, {Component, Fragment} from 'react'

export default class EditMovie extends Component {
    state = {
        movies: {},
        isLoaded: false,
        error: null,
    }
    render() {
        let {movie} = this.state;

        return(
            <Fragment>
                <h2> Add/Edit Movie</h2>
                <hr />
                <form method="post">
                    <div className="mb-3">
                        <label for="title" className="form-lable">
                            Title
                        </label>
                        <input type="text"
                        className="form-control"
                        id="title"
                        name="title"
                        value={movie.title}/>
                    </div>
                </form>
            </Fragment>
        )
    }
}
