import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
export default class OneGenre extends Component {
    state = {
        flock: [],
        isLoaded: false,
        error: null,
        chickName: "",
    };

    componentDidMount() {
        fetch("http://localhost:9000/v1/kukuchic/flock")
            .then((response) => {
                if (response.status !== "200") {
                    let err = Error;
                    err.message = "Invalid response code: " + response.status;
                    this.setState({ error: err });
                }
                return response.json();
            })
            .then((json) => {
                this.setState(
                    {
                        flock: this.flock,
                        isLoaded: true,
                        chickName: this.props.location.genreName,
                    },
                    (error) => {
                        this.setState({
                            isLoaded: true,
                            error,
                        });
                    }
                );
            });
    }

    render() {
        let { flock, isLoaded, error, chickName } = this.state([]);

        if (!flock) {
            flock = [];
        }

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <p>Loading...</p>;
        } else {
            return (
                <Fragment>
                    <h2>Chick: {chickName}</h2>

                    <div className="list-group">
                        {flock.map((m) =>(
                            <Link
                                key={m.id}
                                to={`/flocks/${m.id}`}
                                className="list-group-item list-group-item-action"
                             >
                                {m.operations}

                            </Link>
                        ))}
                    </div>
                </Fragment>
            );
        }
    }
}
