import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
export default class OneGenre extends Component {
    state = {
        flock: [],
        isLoaded: false,
        error: null,
        chickName: "",
    };

    componentDidMount() {
        fetch("http://localhost:9000/v1/kukuchic/flock/"+ this.props.match.params.id)
            .then((response) => {
                if (response.status !== "200") {
                    let err = Error;
                    err.message = "Invalid response code: " + response.status;
                    this.setState({ error: err });
                }
                return response.json();
            })
            .then((json) => {
                this.setState(
                    {
                        flock: json.flock,
                        isLoaded: true,
                        chickName: this.props.location.genreName,
                    },
                    (error) => {
                        this.setState({
                            isLoaded: true,
                            error,
                        });
                    }
                );
            });
    }

    render() {
        let { flock, isLoaded, error, chickName } = this.state;

        if (!flock) {
            flock = [];
        }

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <p>Loading...</p>;
        } else {
            return (
                <Fragment>
                    <h2>Chick: {chickName}</h2>

                    <div className="list-group">
                        {
                            <div>
                                <Link
                                    key={flock.operations.op_pro}
                                    to={`/flock/${flock.operations.op_pro}`}
                                    className="list-group-item list-group-item-action"
                                >
                                    {flock.operations.op_pro}

                                </Link>
                                <Link
                                    key={flock.operations.op_hat}
                                    to={`/flocks/${flock}`}
                                    className="list-group-item list-group-item-action"
                                >
                                    {flock.operations.op_hat}

                                </Link>
                            </div>
                        }
                    </div>
                </Fragment>
            );

        }
    }
}
